    private void btnmahoaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnmahoaActionPerformed
        int k =  Integer.valueOf(this.txtkhoa.getText());
        String s = this.txtvanban.getText();
        int n = s.length();
        int sd,sc;
        sd = k;
        sc=n/sd+1;
        char hr[][] = new char[sd][sc];
        int c,d;
        c=0;d=0;
        int sodu=n%sd;
        for(int i=0;i<n;i++){
        hr[d][c] = s.charAt(i);
        d++;
        if(d==k){
        c++;d=0;
        }
        }
        String kq="";
        int sokytu=sc;
        for(int i=0;i<sd;i++){
         if(i>=sodu)sokytu=sc-1;
         for(int j=0;j<sokytu;j++)
             kq=kq+hr[i][j];
        }
        this.txtmahoa.setText(kq);
    }//GEN-LAST:event_btnmahoaActionPerformed

    private void btngiaimaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btngiaimaActionPerformed
        int k =  Integer.valueOf(this.txtkhoa.getText());
        String s = this.txtmahoa.getText();
        int n = s.length();
        int sd,sc;
        sd = k;
        sc=n/sd+1;
        int t=0;
        char hr[][] = new char[sd][sc];
        for(int i=0;i<sd;i++){
          if(i>=(n%sd))
          for(int j=0;j<sc-1;j++){
            hr[i][j]=s.charAt(t);
            t++;
          }
        }
        int c,d;
        c=0;d=0;
        String kq="";
        for(int i=0;i<n;i++){
          kq+=hr[d][c];
          d++;
          if(d==k){
            c++;d=0;
          }
        }
        this.txtvanban.setText(kq);
    }//GEN-LAST:event_btngiaimaActionPerformed

